mapper.select:
SELECT DISTINCT FROM SYS_MENU SYS_MENU INNER JOIN SYS_MENU_ROLE SYS_MENU_ROLE ON SYS_MENU.ID = SYS_MENU_ROLE.MENU_ID WHERE SYS_MENU.DIS_FLAG = 0 AND SYS_MENU_ROLE.ROLE_ID in
#{item}
ORDER BY NUM ASC

mapper.update:
UPDATE SYS_MENU
CODE=#{code,jdbcType=VARCHAR}, PID=#{pid,jdbcType=VARCHAR}, MENU_REAL_NAME=#{menuRealName,jdbcType=VARCHAR}, ICON=#{icon,jdbcType=VARCHAR}, URL=#{url,jdbcType=VARCHAR}, NUM=#{num,jdbcType=INTEGER}, LEVELS=#{levels,jdbcType=INTEGER}, MENU_FLAG=#{menuFlag,jdbcType=INTEGER}, TIPS=#{tips,jdbcType=VARCHAR}, OPEN_FLAG=#{openFlag,jdbcType=INTEGER}, DIS_FLAG=#{disFlag,jdbcType=VARCHAR}, UPDATE_USER=#{updateUser,jdbcType=VARCHAR}, UPDATE_DATE=#{updateDate,jdbcType=TIMESTAMP},
where ID=#{id,jdbcType=INTEGER}
